import React, { useState, useEffect } from 'react';
import '../styles/scrolleffect.css'; // Assurez-vous d'avoir un fichier CSS approprié

function ScrollEffectComponent() {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY + window.innerHeight;
      const elementPosition = document.getElementsByClassName('scroll-container').offsetTop;

      if (scrollPosition > elementPosition) {
        setIsVisible(true);
      }
    };

    window.addEventListener('scroll', handleScroll);

    // Nettoyage de l'écouteur lors du démontage du composant
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className="scroll-container">
      <div 
        id="elementToAnimate" 
        className={`animated-element ${isVisible ? 'visible' : ''}`}
      >
        Cet élément apparaîtra lorsque vous ferez défiler
      </div>
      <div style={{ height: '2000px' }}>Scroll pour voir l'effet</div>
    </div>
  );
}

export default ScrollEffectComponent;
