import React, { useState, useEffect } from "react";
import "../styles/chocolat.css";
import "../styles/dropdown.css";

export default function MobileDropdown() {
  const [chocolats, setChocolats] = useState([]);
  const [selectedCategories, setSelectedCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");
  const [prixMin, setPrixMin] = useState("");
  const [prixMax, setPrixMax] = useState("");
  const [noteMin, setNoteMin] = useState("");
  const [noteMax, setNoteMax] = useState("");

  const categories = ['Tous les produits', 'blanc', 'lait', 'noir', 'caramel', 'noix', 'fruit', 'liqueur'];

  useEffect(() => {
    fetch("data.json")
      .then((response) => response.json())
      .then((data) => {
        setChocolats(data.chocolats);
        const uniqueCategories = Array.from(new Set(data.chocolats.map(chocolat => chocolat.category)));
        const updatedUniqueCategories = ["Tous les produits", ...uniqueCategories];
        setSelectedCategory(updatedUniqueCategories[0]);
      })
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  const filteredChocolats = chocolats.filter(chocolat => {
    const isCategoryMatch = selectedCategory === 'Tous les produits' || chocolat.category === selectedCategory;
    const isPriceInRange = (prixMin === "" || chocolat.price >= parseFloat(prixMin)) && (prixMax === "" || chocolat.price <= parseFloat(prixMax));
    const isNoteInRange = (noteMin === "" || chocolat.note >= parseFloat(noteMin)) && (noteMax === "" || chocolat.note <= parseFloat(noteMax));
    return isCategoryMatch && isPriceInRange && isNoteInRange;
  });

  const handleReset = () => {
    setPrixMin("");
    setPrixMax("");
    setNoteMin("");
    setNoteMax("");
    setSelectedCategories([]);
  };

  return (
    <>
      {/* Dropdown for selecting category */}
      <div className="mobileDropdownContainer">
        <h4>Catégorie : cliquez ici</h4>
        <div className="mobileDropdownContent">
          <h4>Filtre</h4>
          <select value={selectedCategory} onChange={(e) => setSelectedCategory(e.target.value)}>
            <option value="">Sélectionner une catégorie</option>
            {categories.map((category, index) => (
              <option key={index} value={category}>
                {category}
              </option>
            ))}
          </select>
        </div>
      </div>

      {/* Display filtered chocolates */}
      <div className='fetchdatamobile'>
        {filteredChocolats.map((chocolat) => (
          <section key={chocolat.id}>
            <img src={require(`../images/${chocolat.image}`)} alt="imgjson" />
            <h2>{chocolat.title}</h2>
            <p>prix : {chocolat.price} €</p>
            <p>Note clients : {chocolat.note}</p>
          </section>
        ))}
      </div>

      {/* Display first filtered chocolate details */}
      {filteredChocolats.length > 0 ? (
        <div>
          <h2>{filteredChocolats[0].title}</h2>
          <img src={require(`../images/${filteredChocolats[0].image}`).default} alt={filteredChocolats[0].title} />
          <p>Prix: {filteredChocolats[0].price} €</p>
          <p>Note: {filteredChocolats[0].note}</p>
        </div>
      ) : (
        <p>Aucun produit trouvé pour la catégorie sélectionnée.</p>
      )}
    </>
  );
}
