import { useContext } from "react"
import PanierContext from "../store/panier-context"

export const Cart = ()=>{


    const {items}= useContext(PanierContext)


    return (
    
    <div id="cart">
    
    {items.length === 0 && <p style={{color:"#0000",}}>Votre Panier est vide.</p>}
    {items.length> 0 && <ul className="cart-items"></ul>}


    </div>
);

}
