import React, { useEffect, useRef } from 'react';
import './styles/App.css'; // Corrected import path
import Homepage from './pages/Homepage';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import PageFicheProduit from './pages/PageFicheProduit';
import BoutiqueSection from './pages/BoutiqueSection';
import Footer from './components/footer';
import { PanierContextProvider } from './store/panier-context';
import Navbar from './components/Navbar';
import $ from 'jquery';

export default function App() {
    const lazyScrollLoadingRef = useRef(null);

    useEffect(() => {
        if (lazyScrollLoadingRef.current) {
            $(lazyScrollLoadingRef.current).lazyScrollLoading({
                lazyItemSelector: '.lazyItem',
                onLazyItemFirstVisible: function(e, $lazyItems, $firstVisibleLazyItems) {
                    $firstVisibleLazyItems.each(function() {
                        this.innerHTML = this.getAttribute('lazy-text');
                    });
                }
            });
        }
    }, []);

    return (
        <Router>
            <PanierContextProvider>
                <div id="lazyScrollLoading" ref={lazyScrollLoadingRef} className="App">
                    <Navbar />
                    <Switch>
                        <Route path="/" exact component={Homepage} />
                        <Route path="/boutique" component={BoutiqueSection} />
                        <Route path="/ficheproduit" component={PageFicheProduit} />
                    </Switch>
                    <Footer />
                </div>
            </PanierContextProvider>
        </Router>
    );
}
