    }} className={`badge text-bg-${cart[chocolat.id] > 0 ? 'success' : 'error'}`}
  >{cart[chocolat.id] || 0}
  </span><img id="panier" src={require("../images/panier.png")} alt="panier" />
        </div>
    );
  }
