import React, { useEffect, useState } from 'react';
import './ScrollFade.css'; // Importation des styles CSS

function ScrollFade() {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    // Fonction pour mettre à jour l'état de visibilité
    const updateVisibility = () => {
      const element = document.getElementById('target');
      const scrollPosition = window.scrollY;
      const windowHeight = window.innerHeight;
      const elementPosition = element.offsetTop;

      if (scrollPosition > elementPosition - windowHeight + 100) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };

    // Ajouter l'écouteur d'événement de défilement
    window.addEventListener('scroll', updateVisibility);

    // Nettoyage de l'écouteur d'événement lors de la suppression du composant
    return () => {
      window.removeEventListener('scroll', updateVisibility);
    };
  }, []);

  return (
    <div className={`fade-in ${isVisible ? 'visible' : ''}`} id="target">
      Cet élément apparaîtra en fondu au défilement.
    </div>
  );
}

export default ScrollFade;
