import React, { useState, useEffect } from 'react';
import chocolatsData from "../store/data.json"; // Assurez-vous que le chemin est correct
import "../styles/ficheproduit.css";

export default function PageFicheProduit() {
  const [chocolats, setChocolats] = useState([]);

  useEffect(() => {
    // Utilisation de l'importation directe
    setChocolats(chocolatsData.chocolats);
  }, []);

  return (
    <div>
      <h5 className="titleBoutiquesection">NOM PRODUIT</h5>
      {chocolats.map((chocolat) => (
        <div id="containerficheproduit" key={chocolat.id}>
          <img src={require(`../images/${chocolat.image}`).default} alt="imgjson" />
          <h2>{chocolat.title}</h2>
          <p>prix : {chocolat.price} €</p>
          <p>Note clients : {chocolat.description}</p>
          {/* Vous pouvez ajouter d'autres éléments ici si nécessaire */}
        </div>
      ))}
    </div>
  );
}
