import React, { useState, useEffect } from 'react';

const MobileDropdown = ({ chocolats }) => {
  const [selectedCategory, setSelectedCategory] = useState('');
  const [categories, setCategories] = useState([]);

  // Charger les catégories depuis le fichier JSON (ou une API)
  useEffect(() => {
    // Supposons que vous utilisiez fetch pour charger les données
    fetch('data.json')
      .then(response => response.json())
      .then(data => setCategories(data))
      .catch(error => console.error('Error loading categories:', error));
  }, []);

  const handleCategoryChange = (event) => {
    setSelectedCategory(event.target.value);
    // Ajoutez ici toute autre logique que vous souhaitez exécuter lorsque la catégorie est sélectionnée
  };

  return (
    <div className="mobileDropdownContainer">
      <h4>Category</h4>
      <div className="mobileDropdownContent">
        <h4>Filter</h4>
        <select value={selectedCategory} onChange={handleCategoryChange}>
          <option value="">Select a category</option>
          {categories.map((category, index) => (
            <option key={index} value={category}>
              {category}
            </option>
          ))}
        </select>
      </div>
    </div>
  );
};

export default MobileDropdown;
