import React, { useState } from 'react';
import "../styles/formproduct.css";
import { useForm } from 'react-hook-form';
// import ListeChocolats from "../pages/pageficheproduitjson"; // Not used
// import { title } from 'node-openssl-cert/name_mappings'; // Not used

export default function Formproduct() {
  const products = [
    { id: "4" ,
    title: "Blanc Passion",
    price: 5.99,
    note : 5,
    image: "produit4.jpg", },
    // Add more products as needed
  ];

  const [filteredProducts, setFilteredProducts] = useState(products); // Start with all products

  const filterProducts = (image) => {
    const filtered = products.filter(image => product.image === image);
    setFilteredProducts(filtered);
  }

  return (
    <>
      <div className="containerForm">
        <h4>Filtre </h4>
        <div className="categoriepanel">
          <label className="categ" htmlFor="category">Catégories</label><br /><br />
          {/* Sample filter for Blanc chocolate */}
          <input type="checkbox" id="Panel1form" name="ChocolatBlanc" value="product" onClick={() => filterProducts(1)} />
          <label htmlFor="Panel1form"> Chocolat Blanc</label><br />
          {/* Add more checkboxes and filter functions as needed */}
        </div>

        {/* ... (Rest of your code remains unchanged) ... */}

      </div>

      {/* Affichage des produits filtrés */}
      <ul>
        {filteredProducts.map(product => (
          <li key={product.id}>
            {product.title} - {product.price}€
          </li>
        ))}
      </ul>
    </>
  );
}
