import React, { useEffect, useState } from "react";
import "../styles/Boutique.css";
import "../styles/chocolat.css";
import  ListeChocolats from "../pages/pageficheproduitjson";
import Panier from "./components/Panier";

// import { Panier } from "../components/Button";

export default function BoutiqueSection() {
  const [cart, setCart] = useState({});

  const handleAddToCart = (chocolatId) => {
    setCart(prevCart => ({
      ...prevCart,
      [chocolatId]: (prevCart[chocolatId] || 0) + 1
    }));
  };

  const handleEmptyCart = () => {
    setCart({});
  };
    return (
     <>
          <h1 className="titleBoutiquesection">BOUTIQUE</h1>
          <Panier cart={cart} handleEmptyCart={handleEmptyCart} />
          <article />
        <ListeChocolats />
        
      </>
    );
  }
