import React, { useState, useEffect } from 'react';
import ListeChocolats from '../pages/pageficheproduitjson';

const ImageGallery = () => {
  const [categoryFilter, setCategoryFilter] = useState('all');
  const [chocolats, setChocolats] = useState([]);
  const [filteredImages, setFilteredImages] = useState([]);

  useEffect(() => {
    fetch('data.json')
      .then(response => response.json())
      .then(data => {
        setChocolats(data.chocolats);
        setFilteredImages(data.chocolats);  // Initialize with all chocolates
      })
      .catch(error => console.log(error));
  }, []);

  useEffect(() => {
    if (categoryFilter === 'all') {
      setFilteredImages(chocolats);
    } else {
      const filtered = chocolats.filter(chocolat => chocolat.category === categoryFilter);
      setFilteredImages(filtered);
    }
  }, [chocolats, categoryFilter]);

  return (
    <section>
  <ListeChocolats/>
    </section>
  );
}

export default ImageGallery;
