import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';
import Dropdown from './Dropdown';

function Navbar() {
  const [click, setClick] = useState(false);
  const [dropdown, setDropdown] = useState(false);
  const [color, setColor] = useState(false);

  const changeColor = () => {
    setColor(window.scrollY >= 80);
  };

  useEffect(() => {
    window.addEventListener('scroll', changeColor);
    return () => {
      window.removeEventListener('scroll', changeColor);
    };
  }, []);

  //

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const onMouseEnter = () => {
    setDropdown(window.innerWidth >= 960);
  };

  const onMouseLeave = () => {
    setDropdown(false);
  };

  return (
    // <nav className={color ? 'navbar1 navbar-bg' : 'navbar1'}>

    <nav className="navbar">
    <div className="navbar_logo">Logo</div>
    <ul className="navbar_links">
    <li className="navbar_item">
    < a href="/" className="navbar_link">Accueil</a>
    </li>
    <li className="navbar_item">
    < a href="/" className="navbar_link">Boutique</a>
    </li>
    <li className="navbar_item">
    < a href="/" className="navbar_link">Mon Compte</a>
    </li>
    </ul>
    <button className="navbar_burger"></button>
    <span className="burger-bar"></span>
    </nav>
  );
}

export default Navbar;
