import React, { useState, useEffect } from 'react';
import ListeChocolats from '../pages/pageficheproduitjson';  // Assurez-vous que cette importation est correcte.



const ImageGallery = () => {
  const [categoryFilter, setCategoryFilter] = useState('all'); // all or nature or city...
  const [chocolats, setChocolats] = useState([]);

  useEffect(() => {
    fetch('data.json')
      .then(response => response.json())
      .then(data => setChocolats(data.chocolats))
      .catch(error => console.log(error));
  }, []);

  const filteredImages = chocolats.filter(chocolat => {
    if (categoryFilter === 'all') return true;
    return chocolat.category === categoryFilter; // Assurez-vous que chaque objet chocolat a une propriété 'category'.
  });

  return (
    <article>
      {/* Utilisez filteredImages pour afficher les images filtrées si nécessaire */}
      {/* Pour l'instant, vous pouvez laisser ListeChocolats afficher tous les chocolats */}
      <ListeChocolats chocolats={chocolats} />
    </article>
  );
}

export default ImageGallery;
