import React, { useState, useEffect } from "react";
import "../styles/chocolat.css";
import "../styles/dropdown.css";

export default function MobileDropdown() {
  // State
  const [chocolats, setChocolats] = useState([]);
  const [selectedCategories, setSelectedCategories] = useState([]);
  const [prixMin, setPrixMin] = useState("");
  const [prixMax, setPrixMax] = useState("");
  const [noteMin, setNoteMin] = useState("");
  const [noteMax, setNoteMax] = useState("");

  // Static data
  const categories = ['Tous les produits', 'blanc', 'lait', 'noir', 'caramel', 'noix', 'fruit', 'liqueur'];

  // Fetch data and initialize categories
  useEffect(() => {
    fetch("data.json")
      .then((response) => response.json())
      .then((data) => {
        setChocolats(data.chocolats);
        const uniqueCategories = Object.keys(data.chocolats.reduce((acc, chocolat) => ({ ...acc, ...chocolat.category }), {}));
        const updatedCategories = ["Tous les produits", ...uniqueCategories];
        setSelectedCategories(updatedCategories);
      })
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  // Event handlers
  const handlePrixMinChange = (e) => setPrixMin(e.target.value);
  const handlePrixMaxChange = (e) => setPrixMax(e.target.value);
  const handleNoteMinChange = (e) => setNoteMin(e.target.value);
  const handleNoteMaxChange = (e) => setNoteMax(e.target.value);

    const handleCategoryChange = (category) => {
      let updatedCategories;
      
      if (category === "Tous les produits") {
        updatedCategories = selectedCategories.includes(category) ? [] : ["Tous les produits"];
      } else {
        updatedCategories = selectedCategories.includes(category)
          ? selectedCategories.filter((cat) => cat !== category)
          : [...selectedCategories, category];
        
        const tousLesProduitsIndex = updatedCategories.indexOf("Tous les produits");
        if (tousLesProduitsIndex !== -1) {
          updatedCategories.splice(tousLesProduitsIndex, 1);
        }
      }
      
      setSelectedCategories(updatedCategories);
      console.log(updatedCategories);
    };

    const handleReset = () => {

      
      setPrixMin("");
      setPrixMax("");
      setNoteMin("");
      setNoteMax("");
      
    setSelectedCategories([]);

  return (
    <>
      <div className="mobileDropdownContainer">
        <h4>Catégorie</h4>
        <div className="mobileDropdownContent">
          <h4>Filtre</h4>
          <select value={selectedCategories} onChange={handleCategoryChange}>
            <option value="">Sélectionner une catégorie</option>
            {categories.map((category, index) => (
              <option key={index} value={category}>
                {category}
              </option>
            ))}
          </select>
        </div>
      </div>
  
      <div className='fetchdatamobile'>
        {/* Affichez seulement l'élément sélectionné */}
        {chocolats.length > 0 && (
          <section key={chocolats[0].id}>
            <img src={require(`../images/${chocolats[0].image}`)} alt="imgjson" />
            <h2>{chocolats[0].title}</h2>
            <p>prix : {chocolats[0].price} €</p>
            <p>Note clients : {chocolats[0].note}</p>
          </section>
        )}
      </div>
    </>
  );
        }}
