import React, { useState } from 'react';
import "../styles/formproduct.css";

export default function Formproduct() {
  // Your products array...

  const [products, setProducts] = useState(/* your products array here */);
  const [filteredProducts, setFilteredProducts] = useState(products);
  const [filterValue, setFilterValue] = useState('');

  const filterProducts = (title) => {
    if (!title) {
      setFilteredProducts(products);
    } else {
      const filtered = products.filter(product => product.title.toLowerCase().includes(title.toLowerCase()));
      setFilteredProducts(filtered);
    }
  }

  const toggleFilter = () => {
    if (filterValue) {
      setFilteredProducts(products);
      setFilterValue('');
    } else {
      filterProducts(filterValue);
    }
  };

  return (
    <>
      <input 
        type="text" 
        placeholder="Filtrer par titre..." 
        value={filterValue} 
        onChange={(e) => setFilterValue(e.target.value)} 
      />

      {/* Display filtered products */}
      
      <div className="filterdisplayproducts">
        {filteredProducts.map(product => (
          <li key={product.id}><br/>
            {product.title}<br/> 
            {product.price}â‚¬ <br/>
            Note des clients : {product.note} <br/>
            <span id="learnmorefilter">En savoir plus</span>
          </li>
        ))}
      </div>
    </>
  );
}
