  const filteredChocolats = useMemo(() => {
    // ... (existing filtering logic)
  }, [chocolats, selectedCategories]);

  return (
    <>
      <h1 className="titleficheproduit">LES PRODUITS</h1>
      <div className="containerficheproduct">
        {filteredChocolats.map((chocolat) => (
          <ChocolatItem
            key={chocolat.id}
            chocolat={chocolat}
            cart={cart}
            onAddToCart={handleAddToCart}
            onRemoveFromCart={handleRemoveFromCart}
          />
        ))}
      </div>
      <BasketItem 
        chocolats={chocolats}
        cart={cart}
        handleOpenCart={() => { /* Implement cart opening logic */ }}
        addItemToCart={addItemToCart}
      />
    </>
  );
}



