import React, { useState } from 'react';
import '../styles/ficheproduit.css';
import "../styles/dropdown.css";

const [categories, setCategories] = useState([]);

const MobileDropdown = ({ categories, selectedCategories, handleCategoryChange, handleReset }) => {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  const handleCategoryClick = () => {
    // Ouvrir le menu déroulant seulement si la catégorie est actuellement fermée
    if (!isDropdownOpen) {
      setIsDropdownOpen(true);
    }
  };

  return (
    <div className="mobileDropdownContainer">
      <h4 onClick={toggleDropdown}>Filtre</h4>
      {isDropdownOpen && (
        <div className="mobileDropdownContent">
          <h4 onClick={handleCategoryClick}>Catégorie</h4>
          {categories.map((category) => (
            <div key={category}>
              <input
                type="checkbox"
                id={`MobilePanelForm_${category}`}
                name={category}
                checked={selectedCategories.includes(category)}
                onChange={() => handleCategoryChange(category)}
              />
              <label htmlFor={`MobilePanelForm_${category}`}>{category}</label>
            </div>
          ))}
          {/* ... Autres éléments comme Prix et Notes ... */}
          <span className="resetbutt" onClick={handleReset}>Reset all</span>
        </div>
      )}
    </div>
  );
};

export default MobileDropdown;
