import React, { useState, useEffect } from "react";
import "../styles/Navbar.css";
import "../styles/chocolat.css";
import chocolat from "../pages/pageficheproduitjson";

// import "../styles/Bootstrap.css";

function Navbar() {
  const [showLinks, setshowLinks] = useState(false); // menu fermer par défault
  const [color, setColor] = useState(false); 
  const chocolatId = "your_specific_chocolat_id_here"; // Replace with your actual chocolatId value

  const handleShowLinks = () => {
    setshowLinks(!showLinks); // activation du sholinks si true sinon false
  };

  const [cart, setCart] = useState({});

  const handleAddToCart = (chocolatId) => {
    setCart((prevCart) => ({
      ...prevCart,
      [chocolatId]: (prevCart[chocolatId] || 0) + 1,
    }));
  };

  const handleValidateCart = () => {
    alert("Votre panier a été validé avec succès !");
  };

  const handleEmptyCart = () => {
    setCart({});
  };
  const changeColor = () => {
    setColor(window.scrollY >= 90);
  };

  useEffect(() => {
    window.addEventListener('scroll', changeColor);
    return () => {
      window.removeEventListener('scroll', changeColor);
    };
  }, []);



  return (

    <nav key={chocolat.id} className={color ? 'fixed navbar navbar-bg' : 'fixed navbar'}>
    <nav className={`fixed navbar ${showLinks ? "show-nav" : "hide-nav"} `}>
      <div className="navbar_logo ">
        <img
          id="logomenucoverdesktop"
          src={require("../images/logo.png")}
          alt="logomobilecover"
        />
      </div>
      <ul className="navbar_links">
        <li className="navbar_item slideInDown-1">
          <a href="/" className="navbar_link">
            Accueil
          </a>
        </li>
        <li className="navbar_item slideInDown-2">
          <a href="/boutique" className="navbar_link">
            Boutique
          </a>
        </li>
        <li className="navbar_item slideInDown-3">
          <a href="/" className="navbar_link">
            Panier
          </a>
        </li>
        <li className="navbar_item slideInDown-4">
          <img
            id="logomenucovermobile"
            src={require("../images/logo.png")}
            alt="logomobilecover"
          />
        </li>
        <li className="navbar_item">        
            <div>
             <span class="badge text-bg-success">{cart[chocolat.id] || 0}</span>
              <img id="panier" src={require("../images/panier.png")}  alt="panier" />
            </div>
             </li>
      </ul>
      <button className="navbar_burger"></button>
      <div className="burger-bar" onClick={handleShowLinks}></div>
      </nav></nav>
  );
}

export default Navbar;
