import React, { useState, useEffect, useContext } from "react";
import "../styles/ficheproduit.css";
import PanierContext from "../store/panier-context";

export default function PageFicheProduit() {
  const [chocolats, setChocolats] = useState([]);

  useEffect(() => {
    fetch("data.json")
      .then((response) => response.json())
      .then((data) => {
        setChocolats(data.chocolats);
      });
  }, []); // Le tableau vide signifie que ce hook s'exécutera une seule fois après le montage initial
  const{addItemToCart}=useContext(PanierContext);

  const [cart, setCart] = useState({});
  const handleAddToCart = (chocolatId) => {
    setCart((prevCart) => ({
      ...prevCart,
      [chocolatId]: (prevCart[chocolatId] || 0) + 1,
    }));
  };

  const handleRemoveFromCart = (chocolatId) => {
    if (cart[chocolatId] > 1) {
      setCart((prevCart) => ({
        ...prevCart,
        [chocolatId]: prevCart[chocolatId] - 1,
      }));
    } else {
      const newCart = { ...cart };
      delete newCart[chocolatId];
      setCart(newCart);
    }
  };

  return (

        <><h1 className="titleBoutiquesection">NOM  PRODUIT</h1><article className="containerficheproduct">

      {chocolats.map((chocolat) => (
        <section key={chocolat.id}>
          <img src={require(`../images/${chocolat.image}`).default} alt="imgjson" />
          <h2>{chocolat.title}</h2>
          <p>prix : {chocolat.price} €</p>
          <p>Description du produit :<br /> {chocolat.description}</p>

          {cart[chocolat.id] ? (
            <>
              <span className="buttvalidate" onClick={() => addItemToCart(chocolat.id)}>Ajouter au panier</span>

              <button className="buttvalidate" onClick={() => handleRemoveFromCart(chocolat.id)}>-</button>
              <span>{cart[chocolat.id]}</span>
              <button className="buttvalidate" onClick={() => handleAddToCart(chocolat.id)}>+</button>
            </>
          ) : (
            <span className="buttvalidate" onClick={() => handleAddToCart(chocolat.id)}>Ajouter au panier</span>

          )}

          <div></div>
        </section>
      ))}
    </article></>
  );
}
