import React from "react";
import { useState } from "react";
import Navbar from "./components/Navbar";
import "./styles/App.css";
import Homepage from "./pages/Homepage";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import PagePanier from "./pages/PagePanier";
import PageFicheProduit from "./pages/PageFicheProduit";
import BoutiqueSection from "./pages/BoutiqueSection";

// import Productcarousel from "./components/Productcarousel";
import Footer from "./components/footer";


function App() {
  const [cart, setCart] = useState({});

  // calcul quantité produits dans le logo panier 
  const handleAddToCart = (chocolatId) => {
    setCart((prevCart) => ({
      ...prevCart,
      [chocolatId]: (prevCart[chocolatId] || 0) + 1,
    }));
  };

  return (
    <div className="App">
     <img 
        id="panier" 
        src={require("../images/panier.png")} 
        alt="panier" 
        onClick={() => handleAddToCart("produitIdDuPanier")} // Remplacez "produitIdDuPanier" par l'ID réel de votre produit
      />
    <Router>
    <Navbar cart={cart} />
      <Switch>
        <Route path="/" exact component={Homepage} />
        <Route path="/boutique" component={BoutiqueSection} />
        <Route path="/panier" component={PagePanier} />
        <Route path="/ficheproduit" component={PageFicheProduit} />
      </Switch>
      {/* <Productcarousel /> */}
      <Footer />
    </Router>

    </div>
  );
}

export default App;
