import React from 'react';
import Badge from 'react-bootstrap/Badge';

function BasketItem({ chocolat, cart, handleOpenCart, addItemToCart }) {
    return (
        <div className="basket">
            <Badge 
                bg={cart[chocolat.id] > 0 ? "success" : "danger"} 
                onClick={() => {
                    handleOpenCart();
                    addItemToCart(chocolat.id);
                }}
            >
                {cart[chocolat.id] || 0}
            </Badge>
            <img id="panier" src={require("../images/panier.png")} alt="panier" />
        </div>
    );
}

export default BasketItem;
