import React, { useState, useEffect } from 'react';

// Importer les données JSON en utilisant require
const categoriesData = require('../store/data.json');

const MobileDropdown = () => {
  const [selectedCategory, setSelectedCategory] = useState('');
  const [chocolats, setCategories] = useState([]);

  // Charger les catégories depuis le fichier JSON
  useEffect(() => {
    setCategories(categoriesData);
  }, []);

  const handleCategoryChange = (event) => {
    setSelectedCategory(event.target.value);
    // Ajoutez ici toute autre logique que vous souhaitez exécuter lorsque la catégorie est sélectionnée
  };

  return (
    <div className="mobileDropdownContainer">
      <h4>Category</h4>
      <div className="mobileDropdownContent">
        <h4>Filter</h4>
        <select value={selectedCategory} onChange={handleCategoryChange}>
          <option value="">Select a category</option>
          {chocolats.map((category, index) => (
            <option key={index} value={category}>
              {category}
            </option>
          ))}
        </select>
      </div>
    </div>
  );
};

export default MobileDropdown;
