import React, { useState } from 'react';
import { Button } from './Button';
import { Link } from 'react-router-dom';
import './Navbar.css';
import Dropdown from './Dropdown';

function Navbar() {
  const [click, setClick] = useState(false);
  const [dropdown, setDropdown] = useState(false);

  // changer couleur navbar au scroll 

  const [color, setColor] = useState(false)
  const changeColor = () => {
    if (window.scrollY >=80){

      setColor(true)
    }else{

      setColor(false)
    }

  }

  window.addEventListener('scroll', changeColor);

  //
  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const onMouseEnter = () => {
    if (window.innerWidth < 960) {
      setDropdown(false);
    } else {
      setDropdown(true);
    }
  };

  const onMouseLeave = () => {
    if (window.innerWidth < 960) {
      setDropdown(false);
    } else {
      setDropdown(false);
    }
  };

  return (


      <nav className={color ? 'navbar1 navbar-bg' : 'navbar1'}>
      <div className='navbar'>
        <Link to='/' className='navbar-logo' onClick={closeMobileMenu}>
          <img id='logo' src={require('./images/logo.png')} alt='logo' />
        </Link>

        <div className='menu-icon' onClick={handleClick}>
          <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
        </div>
        <ul className={click ? 'nav-menu active' : 'nav-menu'}>
          <li className='nav-item'>
            <Link to='/' className='nav-links' onClick={closeMobileMenu}>
              Accueil
            </Link>
          </li>
          <li
            className='nav-item'
            onMouseEnter={onMouseEnter}
            onMouseLeave={onMouseLeave}
          >
            <Link to='/services' className='nav-links' onClick={closeMobileMenu}>
              Boutique <i className='fas fa-caret-down' />
            </Link>
            {dropdown && <Dropdown />}
          </li>
          <li className='nav-item'>
            <Link
              to='/sign-up'
              className='nav-links-mobile'
              onClick={closeMobileMenu}
            >
              Connexion Compte
            </Link>
          </li>
        </ul>
        <img id='panier' src={require('./images/white.jpg')} alt='panier' />
      </div>
    </nav>
  );
}

export default Navbar;
