import { useContext } from "react";
import PanierContext from "../store/panier-context";

 const Cart = ()=>{


    const {items}= useContext(PanierContext)


    return (
    
    <div id="cart">
  
    {items.length === 0 && (
    
    <p style={{color:"#000",

    }}
    
    >
        Votre Panier est vide.
        
    </p>
        
    )}


    {items.length > 0 && <ul className="cart-items"></ul>}
    {items.map((product)=> {

        return <li key={product.id} className="flex">

           <div className="flex flex-col">
            <span className="title">{product.title}</span><br/>
            <span className="price">{product.price} Euros</span>
            
            </div> 

            <div className="cart-box-actions flex">
            <button className="redvalue"> - </button>
            <span className="price-quantity">{product.quantity}</span>
            <button className="addvalue"> + </button>
            
            </div>
        </li>
    }
    
    
    )}

    </div>
);

};

export default Cart;
