import React, { useMemo } from 'react';

const ListeChocolats = ({ chocolats }) => {
  const memoizedImages = useMemo(() => {
    const images = chocolats.map(chocolat => {
      const imageUrl = require(`../images/${chocolat.image}`).default;
      return imageUrl;
    });
    return images;
  }, [chocolats]);

  return (
    <div>
      {chocolats.map(chocolat => (
        <section key={chocolat.id}>
          <img src={memoizedImages[chocolat.id]} alt={chocolat.title} />
          <h2>{chocolat.title}</h2>
          <p>prix : {chocolat.price} â‚¬</p>
          <p>Note clients : {chocolat.note}</p>
          <span>Ajout au panier</span>
        </section>
      ))}
    </div>
  );
};

export default ListeChocolats;
