import React, { useState, useEffect, useMemo } from 'react';
import '../styles/ficheproduit.css';
import "../styles/dropdown.css";

const MobileDropdown = ({ chocolats }) => {
  const [selectedCategories, setSelectedCategories] = useState([]);
  const [prixMin, setPrixMin] = useState("");
  const [prixMax, setPrixMax] = useState("");
  const [noteMin, setNoteMin] = useState("");
  const [noteMax, setNoteMax] = useState("");

  // Effect to load categories
  useEffect(() => {
    // Simulate loading categories from an API or data source
    const categoriesFromApi = ["Category1", "Category2", "Category3"];
    setSelectedCategories(["All Products", ...categoriesFromApi]);
  }, []);

  // Memoized filtered chocolates
  const filteredChocolats = useMemo(() => {
    return chocolats.filter((chocolat) => {
      const isTousLesProduitsSelected = selectedCategories.includes("All Products");
      const isCategoryMatch = isTousLesProduitsSelected || selectedCategories.length === 0 || selectedCategories.some((category) => chocolat.category === category);
      const isPriceInRange = (prixMin === "" || chocolat.price >= parseFloat(prixMin)) && (prixMax === "" || chocolat.price <= parseFloat(prixMax));
      const isNoteInRange = (noteMin === "" || chocolat.note >= parseFloat(noteMin)) && (noteMax === "" || chocolat.note <= parseFloat(noteMax));
      return isCategoryMatch && isPriceInRange && isNoteInRange;
    });
  }, [chocolats, selectedCategories, prixMin, prixMax, noteMin, noteMax]);

  const handleCategoryChange = (category) => {
    setSelectedCategories((prevCategories) => {
      if (category === "All Products") {
        return prevCategories.includes(category) ? [] : ["All Products"];
      } else {
        const updatedCategories = [...prevCategories];
        const index = updatedCategories.indexOf(category);
        index === -1 ? updatedCategories.push(category) : updatedCategories.splice(index, 1);
        const allProductsIndex = updatedCategories.indexOf("All Products");
        if (allProductsIndex !== -1) updatedCategories.splice(allProductsIndex, 1);
        return updatedCategories;
      }
    });
  };

  const handleReset = () => {
    setPrixMin("");
    setPrixMax("");
    setNoteMin("");
    setNoteMax("");
    setSelectedCategories([]);
  };

  return (
    <div className="mobileDropdownContainer">
      <h4>Category</h4>
      <div className="mobileDropdownContent">
        <h4>Filter</h4>
        {selectedCategories.map((category) => (
          <div key={category}>
            <input
              type="checkbox"
              id={`MobilePanelForm_${category}`}
              name={category}
              checked={selectedCategories.includes(category)}
              onChange={() => handleCategoryChange(category)}
            />
            <label htmlFor={`MobilePanelForm_${category}`}>{category}</label>
          </div>
        ))}
        <span className="resetButton" onClick={handleReset}>Reset all</span>
      </div>
    </div>
  );
};

export default MobileDropdown;
