import React, { useState, useEffect } from 'react';
import "../styles/formproduct.css";
import { useForm } from 'react-hook-form';

export default function Formproduct() {
  const products = [
    // Vos données de produits ici...
  ];

  const [filteredProducts, setFilteredProducts] = useState(products);

  const filterProducts = (image) => {
    const filtered = products.filter(product => product.image === image);
    setFilteredProducts(filtered);
  }



  return (
    <>
      <div className="containerForm">
        <h4>Filtre </h4>
        <div className="categoriepanel">
          <label className="categ" htmlFor="category">Catégories</label><br /><br />
          <input type="checkbox" 
            id="Panel1form" 
            name="ChocolatBlanc" 
            value="product" 
            onClick={() => filterProducts("produit4.jpg")} />
          <label htmlFor="Panel1form"> Chocolat Blanc</label><br />
          
          <input type="checkbox" 
            id="Panel2form"  // Assurez-vous d'avoir des ID uniques
            name="ChocolatAuLait" 
            value="product" 
            onClick={() => filterProducts("produit9.jpg")} />
          <label htmlFor="Panel2form"> Chocolat au lait</label><br />
          
          {/* Ajoutez d'autres cases à cocher et fonctions de filtrage au besoin */}
        </div>
      </div>

      <div className="filterdisplayproducts">
        {filteredProducts.map(product => (
          <li key={product.image}>
            <br/>
            - {product.title}<br/> 
            - {product.price}€ <br/>
            - Note {product.note}
          </li>
        ))}
      </div>
    </>
  );
}
