  return (
    <div className="containerForm">
      <h4>Filtre </h4>
      {/* ... */}
      <h1 className="titleBoutiquesection">NOM PRODUIT</h1>
      <article className="containerficheproduct">
        {filteredChocolats.map((chocolat) => (
          <ChocolatItem
            key={chocolat.id}
