import React, { useState, useEffect, useContext, useRef } from "react";
import PanierContext from "../store/panier-context";
import CartModal from "../components/CartModal";

export function Panierlogo({ valeurDuPanier }) {

    const [cart, setCart] = useState({});
    const { addItemToCart, removeItemFromCart } = useContext(PanierContext);
    const modalRef = useRef();
    const [chocolat] =useState({});

    const handlOpenCart =()=>{
    
      modalRef.current.open();
    
    }
    
    const handleAddToCart = (chocolatId) => {
      setCart((prevCart) => ({
        ...prevCart,
        [chocolatId]: (prevCart[chocolatId] || 0) + 1,
      }));
    };
    
    const handleRemoveFromCart = (chocolatId) => {
      if (cart[chocolatId] > 1) {
        setCart((prevCart) => ({
          ...prevCart,
          [chocolatId]: prevCart[chocolatId] - 1,
        }));
      } else {
        const newCart = { ...cart };
        delete newCart[chocolatId];
        setCart(newCart);
      }
    };



    return (
            <div className="basket">
                <span onClick={() => { handlOpenCart(); addItemToCart(valeurDuPanier);
                }} 
                className={`badge text-bg-${valeurDuPanier > 0 ? 'success' : 'error'}`}
                >
                    {valeurDuPanier || 0}
                </span>
                <img id="panier" src={require("../images/panier.png")} alt="panier" />
            </div>
        );
    }
  export default Panierlogo;
