// CartContext.js
import React, { createContext, useState, useContext } from 'react';

export const CartContext = createContext();

export const CartProvider = ({ children }) => {
  const [cart, setCart] = useState({});

  const addToCart = (productId) => {
    setCart((prevCart) => ({
      ...prevCart,
      [productId]: (prevCart[productId] || 0) + 1,
    }));
  };

  const validateCart = () => {
    alert("Votre panier a été validé avec succès !");
    // Vous pouvez ajouter ici une logique supplémentaire pour la validation, par exemple, envoyer une requête au serveur, etc.
  };

  return (
    <CartContext.Provider value={{ cart, addToCart, validateCart }}>
      {children}
    </CartContext.Provider>
  );
};

export const useCart = () => {
  const context = useContext(CartContext);
  if (!context) {
    throw new Error('useCart must be used within a CartProvider');
  }
  return context;
};
