import React, { useState, useEffect } from 'react';
import ListeChocolats from '../pages/pageficheproduitjson';

const ImageGallery = () => {
  const [categoryFilter, setCategoryFilter] = useState('all');
  const [chocolats, setChocolats] = useState([]);

  useEffect(() => {
    fetch('data.json')
      .then(response => response.json())
      .then(data => setChocolats(data.chocolats))
      .catch(error => console.log(error));
  }, []);

  const filteredImages = chocolats.filter(chocolat => {
    if (categoryFilter === 'all') return true;
    return chocolat.category === categoryFilter;
  });

  return (
    <article>
      <ListeChocolats chocolats={filteredImages} /> {/* Utilisez filteredImages ici */}
    </article>
  );
}

export default ImageGallery;
