import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';
import Dropdown from './Dropdown';

function Navbar() {

  const [showLinks, setshowLinks] = useState(false); // menu fermer par défault

  const handleShowLinks = () =>{

    setshowLinks(!showLinks) // activation du sholinks si true sinon false
  }

  const [color, setColor] = useState(false);

  const changeColor = () => {
    setColor(window.scrollY >= 80);
  };

  useEffect(() => {
    window.addEventListener('scroll', changeColor);
    return () => {
      window.removeEventListener('scroll', changeColor);
    };
  }, []);

  return (
    //<nav className={color ? 'navbar navbar-bg' : 'navbar'}>
    <nav className="navbar show-nav">
    <div className="navbar_logo">Logo</div>
    <ul className="navbar_links">
    <li className="navbar_item">
    < a href="/" className="navbar_link">Accueil</a>
    </li>
    <li className="navbar_item">
    < a href="/" className="navbar_link">Boutique</a>
    </li>
    <li className="navbar_item">
    < a href="/" className="navbar_link">Mon Compte</a>
    </li>
    </ul>
    <button className="navbar_burger" onClick={handleShowLinks}>
    <span className="burger-bar"></span></button>

    </nav>
  );
}

export default Navbar;
