import { createContext, useReducer } from "react";
import chocolats from "../store/data.json";

export const PanierContext = createContext({
    items: [],
    addItemToCart: () => {},
    updateItemsquantity : ()=>{},
});

const cartReducer = (state, action) => {
    if (action.type === "AJOUTER_DANS_PANIER") {
        const updateShoppingCartItems = [...state.items];
        const existingElement = updateShoppingCartItems.find(
            (cartItem) => cartItem.id === action.payload.productId
        );

        if(action.type === "ACTUALISER_QUANTITE_PRODUIT"){
            const updateShoppingCartItems = [...state.items];
            const existingElement = updateShoppingCartItems.find(
                (cartItem) => cartItem.id === action.payload.productId
            ); 

        };

        const updatedData = {

            ...updateShoppingCartItems[existingElement],
            
        };

        const newQuantity = action.payload.quantity;
        updatedData.quantity += newQuantity;

        
        if (updatedData.quantity <= 0 ){

            //Retirer l'élément du panier

            updateShoppingCartItems.splice(existingElement, 1);
        }else {

            updateShoppingCartItems[existingElement] = updatedData;

        }

        if (existingElement) {
            // Trouver l'index de l'élément existant
            const existingElementIndex = updateShoppingCartItems.findIndex(
                (cartItem) => cartItem.id === action.payload.productId
            );
        
            if (existingElementIndex !== -1) {
                // Si l'élément existe déjà, augmentez la quantité
                updateShoppingCartItems[existingElementIndex].quantity += 1;
        
                // Retournez l'état mis à jour immédiatement
                return { ...state, items: updateShoppingCartItems };
            }
        } else {
            const product = chocolats.chocolats.find(
                (product) => product.id === action.payload.productId
            );

            if (product) {
                updateShoppingCartItems.push({
                    id: product.id,
                    title: product.title,
                    price: product.price,
                    quantity: 1,
                });
            }

            return {
                items: updateShoppingCartItems,
            };
        }
    } else if (action.type === "RETIRER_DU_PANIER") {
        const updatedItems = state.items.filter(
            (item) => item.id !== action.payload.productId
        );
        return { ...state, items: updatedItems };
    }

    return state;
};

export const PanierContextProvider = ({ children }) => {
    const [cartState, cartDispatch] = useReducer(cartReducer, {
        items: [],
    });

    const handleProductToCart = (productId) => {
        cartDispatch({
            type: "AJOUTER_DANS_PANIER",
            payload: { productId, },
        });
    };

    const handleRemoveFromCart = (productId) => {
        cartDispatch({
            type: "RETIRER_DU_PANIER",
            payload: { productId },
        });
    };

    const handleUpdateProductQuantity =(productId, quantity)=>{

        cartDispatch({

            type:"ACTUALISER_QUANTITE_PRODUIT",
            payload : {

                productId,
                quantity,
            },
        });
    }

    const initialValue = {
        items: cartState.items,
        addItemToCart: handleProductToCart,
        removeFromCart : handleRemoveFromCart,
        updateItemsquantity : handleUpdateProductQuantity,
    };

    return (
        <PanierContext.Provider value={initialValue}>
            {children}
        </PanierContext.Provider>
    );
};

export default PanierContext;
