// Panier.js
import "../styles/panier.css"
import React, { useState } from "react";


const [cart, setCart] = useState({});

const handleAddToCart = (chocolatId) => {
  setCart(prevCart => ({
    ...prevCart,
    [chocolatId]: (prevCart[chocolatId] || 0) + 1
  }));
};

const handleEmptyCart = () => {
  setCart({});
};
function Panier({ cart, handleEmptyCart }) {
  return (
    <div className="panier-container">
      <h2>Mon Panier</h2>
      {Object.keys(cart).length === 0 ? (
        <p>Votre panier est vide.</p>
      ) : (
        <>
          <ul>
            {Object.entries(cart).map(([chocolat, quantity]) => (
              <li key={chocolat.id}>
                Chocolat ID: {chocolat} - Quantit√©: {quantity}
              </li>
            ))}
          </ul>
          <button onClick={handleEmptyCart}>Vider le panier</button>
        </>
      )}
    </div>
  );
}

export default Panier;
