import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';
import Dropdown from './Dropdown';

function Navbar() {
  const [click, setClick] = useState(false);
  const [dropdown, setDropdown] = useState(false);
  const [color, setColor] = useState(false);

  const changeColor = () => {
    setColor(window.scrollY >= 80);
  };

  useEffect(() => {
    window.addEventListener('scroll', changeColor);
    return () => {
      window.removeEventListener('scroll', changeColor);
    };
  }, []);

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const onMouseEnter = () => {
    setDropdown(window.innerWidth >= 960);
  };

  const onMouseLeave = () => {
    setDropdown(false);
  };

  return (
    <nav className={color ? 'navbar1 navbar-bg' : 'navbar1'}>
      <div className='navbar'>
        <Link to='/' className='navbar-logo' onClick={closeMobileMenu}>
          <img id='logo' src={require('./images/logo.png')} alt='logo' />
        </Link>

        <div className='menu-icon' onClick={handleClick}>
          <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
        </div>
        <ul className={click ? 'nav-menu active' : 'nav-menu'}>
          <li className='nav-item'>
            <Link to='/' className='nav-links' onClick={closeMobileMenu}>
              Accueil
            </Link>
          </li>
          <li
            className='nav-item'
            onMouseEnter={onMouseEnter}
            onMouseLeave={onMouseLeave}
          >
            <Link to='/services' className='nav-links' onClick={closeMobileMenu}>
              Boutique <i className='fas fa-caret-down' />
            </Link>
            {dropdown && <Dropdown />}
          </li>
          <li className='nav-item'>
            <Link
              to='/sign-up'
              className='nav-links-mobile'
              onClick={closeMobileMenu}
            >
              Connexion Compte
            </Link>
          </li>
        </ul>
        <img id='panier' src={require('./images/white.jpg')} alt='panier' />
      </div>
    </nav>
  );
}

export default Navbar;
