import React, { useState, useEffect } from 'react';


function ChocolateFilter({ chocolats }) {
  const [chocolat, setChocolats] = useState([]);
  const [prixMin, setPrixMin] = useState('');
  const [prixMax, setPrixMax] = useState('');

  useEffect(() => {
    // Récupération des données depuis data.json
    fetch('/path/to/data.json')
      .then(response => response.json())
      .then(data => setChocolats(data))
      .catch(error => console.error('Erreur lors de la récupération des données:', error));
  }, []);


  const [isFilterActive, setFilteredactive] = useState(false); // This line defines the state variables

  const handlePrixMinChange = (e) => {
    setPrixMin(e.price);
  };

  const handlePrixMaxChange = (e) => {
    setPrixMax(e.price);
  };

  const chocolatFiltres = chocolat.filter(chocolat => {
    if (prixMin && prixMax) {
        return chocolat.price >= parseFloat(prixMin) && chocolat.price <= parseFloat(prixMax);
      } else if (prixMin) {
        return chocolat.price >= parseFloat(prixMin);
      } else if (prixMax) {
        return chocolat.price <= parseFloat(prixMax);
      }
      return true;
    });

  return (
    <><div>
          <input
              type="number"
              placeholder="Prix minimum"
              value={prixMin}
              min="0" max="10"
              step="2"
              onChange={handlePrixMinChange} />
              <p>Prix Min</p>
          <input
              type="number"
              placeholder="Prix maximum"
              value={prixMax}
              min="10" max="50"
              step="2"
              onClick={() => setPrixMax("Douceur exotique")} />
              <p>Prix Max</p>

      </div>
      <article>


              {chocolatFiltres.map((e) => (
                  <section key={e.id}>
                      <img src={require(`../images/${e.image}`)} alt="imgjson" />
                      <h2>{e.title}</h2>
                      <p>prix : {e.price} €</p>
                      <p>Note clients : {e.note}</p>
                      <span>Ajout au panier</span>
                  </section>
              ))}
          </article></>

);
}

export default ChocolateFilter;
