import { useContext } from "react";
import PanierContext from "../store/panier-context";

 const Cart = ()=>{


    const {items, updateItemsquantity}= useContext(PanierContext)
    const totalAmount = (items.reduce((acc,item)=> acc+item.amount * item.quantity, 0));
    
    
    

    return (
    
    <div id="cart">
  
    {items.length === 0 && (
    
    <p style={{color:"#000",

    }}
    
    >
        Votre Panier est vide.
        
    </p>
        
    )}


    {items.length > 0 && <ul className="cart-items"></ul>}
    {items.map((product)=> {

        return <li key={product.id} className="flex">

           <div className="flex flex-col">
            <span className="title">{product.title}</span><br/>
            <span className="price">{product.price} Euros</span>
            
            </div> 

            <div className="cart-box-actions flex">
            <span className="redvalue" onClick={()=>updateItemsquantity(product.id,-1)}> - </span>
            <span className="price-quantity">{product.quantity}</span>
            <span className="addvalue"  onClick={()=>updateItemsquantity(product.id,+1)}> + </span>
            
            </div>
        </li>
    }
    
    
    )}

    {

        items.length > 0 && <p id="cart-total-price">

            Montant total : <strong> {totalAmount} EURO{totalAmount>1 ? "s" : ""} </strong>
        </p>
    }

    </div>
);

};

export default Cart;
