import React, { useState } from 'react';
import "../styles/formproduct.css";
import { useForm } from 'react-hook-form';


export default function Formproduct() {
  // Example product data
  const products = [

    {id: "1" ,
    title: "Cerisier",
    price: 2.99,
    note : 5,
    image: "produit1.jpg", }, 

  ]

  const [filteredProducts, setFilteredProducts] = useState(products);

  const filterProducts = (image) => {
    const filtered = products.filter(product => product.image === image);
    setFilteredProducts(filtered);
  }

  return (
    <>

      
      <div className="containerForm">
        <h4>Filtre </h4>
        <div className="categoriepanel">
          <label className="categ" htmlFor="category">Catégories</label><br /><br />


          <input type="checkbox" 
            id="Panel1form" 
            name="ChocolatBlanc" 
            value="product" 
            onClick={() => filterProducts("produit4.jpg")} />
            <label htmlFor="Panel1form"> Chocolat Blanc</label><br />
          {/* Add more checkboxes and filter functions as needed */}

          <input type="checkbox" 
            id="Panel1form" 
            name="Chocolat au lait" 
            value="product" 
            onClick={() => filterProducts("produit9.jpg")} />
             <label htmlFor="Panel1form"> Chocolat au lait</label><br />
          {/* Add more checkboxes and filter functions as needed */}

          <input type="checkbox" 
            id="Panel1form" 
            name="Chocolat noir" 
            value="product" 
            onClick={() => filterProducts("produit8.jpg")} />
             <label htmlFor="Panel1form"> Chocolat noir</label><br />
          {/* Add more checkboxes and filter functions as needed */}

        </div>
      </div>




      {/* Display filtered products */}
      
      <div className="filterdisplayproducts">{filteredProducts.map(product => (
          <li key={product.image}><br/>
            - {product.title}<br/> 
            -{product.price}€ <br/>
            - Note {product.note}
          </li>
        ))}
      </div>
     
    </>
  );
}
