import React, { useEffect, useState } from "react";
import "../styles/Boutique.css";
import "../styles/chocolat.css";

function Chocolat() {

  const[chocolats, setchocolats] = useState([])

  useEffect(() => {

  fetch('data.json')
  .then(response=>response.json())
  .then(data=>setchocolats(data.chocolats))
  .catch(error=>console.log(error))

  }, []);

function ListeChocolats(){
  console.log(chocolats);
   return chocolats.map((v)=>{
    return(
      <section>
      <img src={require(`../images/${v.image}`)} alt="imgjson" />
      <h2>{ v.title }</h2>
      <p>prix : { v.price } â‚¬</p>
      <p>Note clients : { v.note }</p>
      <span>Ajout au panier</span>
      </section>

    );

   });
}
return(
  <article>
    <ListeChocolats />
  </article>
)
}


export default Chocolat
